import React, { Component } from 'react';
import Record from './Record';
import RecordForm from './RecordForm';

import * as RecordsAPI from '../utils/RecordsApi'

class Records extends Component {
  constructor(){
    super();
    this.state={
      error:null,
      isLoad:false,
      records:[]
    }
  }

  componentDidMount(){
    RecordsAPI.getAll().then(
      response =>this.setState({
        isLoad:true,
        records:response.data
      })
    ).catch(
      error => this.setState({
        isLoad:true,
        error
      })
    )    
  }

  addRecord(record){
     this.setState({
      error:null,
      isLoad:true,
      records:[
        ...this.state.records,
        record
      ]
     })
  }

  render() {
    const { isLoad, error, records} =  this.state;
    let recordsComponents;
   if(error){
     recordsComponents = <div>Error: {error.message}</div>; 
   }else if(!isLoad){
    recordsComponents = <div>Loading....</div>;
   }else{
     recordsComponents = ( 
          <table className="table table-bordered">
            <thead>
              <tr>
                <th>Date</th>
                <th>Title</th>
                <th>Amount</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {records.map((record) => <Record key={record.id} {...record}  handelEditRecord={this.update}/>)}
            </tbody>
          </table>      
      );
   }
   return(
    <div className="Records">
    <h2>Records</h2>
    <RecordForm  handelNewReocrd={this.addRecord.bind(this)} />
    {recordsComponents}
    </div>
   )
  }
}

export default Records;
